package com.integrosys.cms.ui.creditriskparam.internallimit.item;import com.integrosys.base.uiinfra.common.CommonForm;public class InternalLimitItemForm extends CommonForm implements		java.io.Serializable {	private static final long serialVersionUID = 1L;	private String description;	private String internalLimitPercentage;	private String currency;	private String capitalFundAmount;	public String getCapitalFundAmount() {		return capitalFundAmount;	}	public void setCapitalFundAmount(String capitalFundAmount) {		this.capitalFundAmount = capitalFundAmount;	}	public String getCurrency() {		return currency;	}	public void setCurrency(String currency) {		this.currency = currency;	}	public String getDescription() {		return description;	}	public void setDescription(String description) {		this.description = description;	}	public String getInternalLimitPercentage() {		return internalLimitPercentage;	}	public void setInternalLimitPercentage(String internalLimitPercentage) {		this.internalLimitPercentage = internalLimitPercentage;	}	public String[][] getMapper() {		return new String[][] { {				"theIntrenalLimitItem",				"com.integrosys.cms.ui.creditriskparam.internallimit.item.InternalLimitItemMapper" } };	}}