package com.integrosys.cms.ui.feed.stockindex.item;import com.integrosys.base.uiinfra.common.CommonForm;/** *  * This class implements FormBean */public class StockIndexItemFormextends CommonFormimplements java.io.Serializable {	public String getIsinCode() {		return isinCode;	}	public void setIsinCode(String isinCode) {		this.isinCode = isinCode;	}	public String getName() {		return name;	}	public void setName(String name) {		this.name = name;	}	public String getRic() {		return ric;	}	public void setRic(String ric) {		this.ric = ric;	}	public String getTicker() {		return ticker;	}	public void setTicker(String ticker) {		this.ticker = ticker;	}	public String getCurrencyCode() {		return this.currencyCode;	}	public String getUnitPrice() {		return this.unitPrice;	}	public void setCurrencyCode(String param) {		this.currencyCode = param;	}	public void setUnitPrice(String param) {		this.unitPrice = param;	}	/**	 * 	 * This method defines a String array which tells what object is to be	 * formed from the form and using what mapper classes to form it.	 * 	 * it has a syntax [(key, MapperClassname)]	 * 	 * 	 * 	 * @return 2-dimensional String Array	 */	public String[][] getMapper() {		return new String[][] { { MAPPER, MAPPER } };	}	public static final String MAPPER = "com.integrosys.cms.ui.feed.stockindex.item.StockIndexItemMapper";	private String ticker = "";	private String name = "";	private String isinCode = "";	private String ric = "";	private String currencyCode = "";	private String unitPrice = "";}