package com.integrosys.cms.ui.report;import com.integrosys.base.uiinfra.common.CommonForm;/** *  * Description: This maps the HTML input form to the ActionForm properties *  *  *  * @author $Author: rohaidah $<br> *  * @version $Revision: 1.1 $ *  * @since $Date: 2003/09/12 12:06:41 $ *  *        Tag: $Name: $ */public class SystemReportSearchForm extends CommonForm {	private String[] name;	private String[] country;	private String searchDate = "";	private String countryCode = "";	public String getCountryCode() {		return countryCode;	}	public void setCountryCode(String countryCode) {		this.countryCode = countryCode;	}	public String getSearchDate() {		return searchDate;	}	public void setSearchDate(String searchDate) {		this.searchDate = searchDate;	}	public String[] getNameList() {		return name;	}	public String getName(int index) {		return name[index];	}	public void setNameList(String[] name) {		this.name = name;	}	public String[] getCountryList() {		return country;	}	public String getCountry(int index) {		return country[index];	}	public void setCountryList(String[] country) {		this.country = country;	}	public int getSize() {		return name.length;	}}